import { LockOutlined } from '@ant-design/icons';
import { css } from '@emotion/css';
import {
  Button,
  Card,
  Col,
  Divider,
  Form,
  FormInstance,
  Row,
  Typography,
} from 'antd';

import { useToggle } from '@/hooks';
import { getColLayout } from '@/utils';
import { IntlMessage } from '@utilComponents/intl-message';

import { UploadAvatar } from '../../../../shared';

import { ModalResetPasswordUser } from './modal-reset-password-user';
import { UserDetailFromInfo } from './user-detail-form-info';
import { UserDetailListOrg } from './user-detail-list-org';

export type UserDetailFormProps = {
  form: FormInstance;
  showOrg?: boolean;
  profile?: string;
  isLoading?: boolean;
  userId?: string;
  showStatus?: boolean;
  isCreate?: boolean;
};

const img_profile_pic =
  'data:image/png;base64,';

export const UserDetailForm = ({
  form,
  showOrg = true,
  isLoading = false,
  showStatus = false,
  userId,
  isCreate,
}: UserDetailFormProps) => {
  const toggle = useToggle();
  console.log('form', form.getFieldsValue());
  return (
    <>
      <Card loading={isLoading}>
        <Form
          form={form}
          layout="vertical"
          initialValues={{
            is_access_end_date: false,
            access_end_date: '',
            profile_pic: img_profile_pic,
            phone_prefix: '+66',
          }}
        >
          <Row>
            <Col
              {...getColLayout([24, 24, 24, 24, 6, 6])}
              className="text-center"
            >
              <Form.Item name="profile_pic">
                <UploadAvatar />
              </Form.Item>
            </Col>

            <Col
              {...getColLayout([24, 24, 24, 24, 18, 18])}
            >
              {' '}
              <Row
                justify={'end'}
                align={'middle'}
                className={css`
                  position: absolute;
                  width: 100%;
                  margin: 0;
                `}
              >
                <Button
                  onClick={() => toggle.edit()}
                  hidden={!showOrg}
                  className={css`
                    position: relative;
                    margin-top: -10px;
                  `}
                  icon={
                    <LockOutlined
                      style={{
                        fontSize: 15,
                        marginRight: '5px',
                      }}
                    />
                  }
                  type="primary"
                  ghost
                >
                  <IntlMessage id="admin.userManagement.user.detail.resetPassword" />
                </Button>
              </Row>
              <Divider
                orientationMargin={0}
                orientation="left"
              >
                <Typography.Title level={3}>
                  <IntlMessage id="admin.userManagement.user.create.basicInfo" />
                </Typography.Title>
              </Divider>
              <UserDetailFromInfo
                showStatus={showStatus}
                isCreate={isCreate}
              />
            </Col>
          </Row>
        </Form>
      </Card>
      {showOrg ? (
        <>
          <UserDetailListOrg userId={userId as string} />
          <ModalResetPasswordUser
            open={toggle.openEdit}
            onCloseModal={() => toggle.edit()}
          />
        </>
      ) : null}
    </>
  );
};
